import React, { useEffect, useState } from 'react';
import { IoLogoClosedCaptioning, IoLogoFacebook } from 'react-icons/io';
import { useDispatch, useSelector } from 'react-redux';
import { getAddress, getCartItems } from '../../actions';
import Layout from '../../components/Layout';
import {
	MaterialButton,
	MaterialInput,
	Anchor,
} from '../../components/MaterialUI';
import PriceDetails from '../../components/PriceDetails';
import AddressForm from './AddressForm';
import Card from "../../components/UI/index.js"
import CartPage from "../CartPage"
import './style.css';
/**
 * @author
 * @function CheckoutPage
 **/

const CheckoutStep = (props) => {
	return (
		<div className='checkoutStep'>
			<div
				onClick={props.onClick}
				className={`checkoutHeader ${props.active && 'active'}`}
			>
				<div>
					<span className='stepNumber'>{props.stepNumber}</span>
					<span className='stepTitle'>{props.title}</span>
				</div>
			</div>
			{props.body && props.body}
		</div>
	);
};

const CheckoutPage = (props) => {
	const user = useSelector((state) => state.user);
	const auth = useSelector((state) => state.auth);
	const [newAddress, setNewAddress] = useState(false);
	const [address, setAddress] = useState([]);
	const [confirmAddress, setConfirmAddress] = useState(false);
	const [selectedAddress, setSelectedAddress] = useState(null);
    const [orderSummary, setOrderSummary] = useState(false)
    const cart = useSelector(state => state.cart)
    const dispatch = useDispatch();



	const onAddressSubmit = (addr) => {
        setSelectedAddress(addr);
        setConfirmAddress(true);
        setOrderSummary(true);
    };


	const selectAddress = (addr) => {
		console.log(addr);
		const updatedAddress = address.map((adr) =>
			adr._id === addr._id
				? { ...adr, selected: true }
				: { ...adr, selected: false },
		);
		setAddress(updatedAddress);
	};

	const confirmDeliveryAddress = (addr) => {
		setSelectedAddress(addr);
		setConfirmAddress(true);
        setOrderSummary(true);
	};

	const enableAddressEditForm = (addr) => {
		const updatedAddress = address.map((adr) =>
			adr._id === addr._id ? { ...adr, edit: true } : { ...adr, edit: false },
		);
		setAddress(updatedAddress);
	};

	useEffect(() => {
		auth.authenticate && dispatch(getAddress());
		auth.authenticate && dispatch(getCartItems());
	}, [auth.authenticate]);

	useEffect(() => {
		const address = user.address.map((adr) => ({
			...adr,
			selected: false,
			edit: false,
		}));
		setAddress(address);
	}, [user.address]);

	const Address = ({
		adr,
		selectAddress,
		enableAddressEditForm,
		confirmDeliveryAddress,
		onAddressSubmit,
	}) => {
		return (
			<div className='flexRow addressContainer'>
				<div>
					<input
						name='address'
						onClick={() => selectAddress(adr)}
						type='radio'
					/>
				</div>
				<div className='flexRow sb addressinfo'>
					{!adr.edit ? (
						<div stye={{ width: '100%' }}>
							<div className='addressDetail'>
								<div>
									<span className='addressName'>{adr.name}</span>
									<span className='addressType'> {adr.addressType} </span>
									<span className='addressMobileNumber'>
										{adr.mobileNumber}
									</span>
								</div>
								{adr.selected && (
									<Anchor
										name='EDIT'
										onClick={() => enableAddressEditForm(adr)}
										style={{
											fontWeight: '500',
											color: '#590a5b',
										}}
									/>
								)}
							</div>
							<div className='fullAddress'>
								{adr.address} <br />
								{`${adr.state}`}
							</div>

							{adr.selected && (
								<MaterialButton
									title='DELIVERY HERE'
									onClick={() => confirmDeliveryAddress(adr)}
									style={{
										width: '200px',
										margin: '10px 0',
									}}
								/>
							)}
						</div>
					) : (
						<AddressForm
							withoutLayout={true}
							onSubmitForm={onAddressSubmit}
                            initialData={adr}
							onCancel={() => {}}
						/>
					)}
				</div>
			</div>
		);
	};



	return (
		<Layout>
			<div className='cartContainer' style={{ alignItems: 'flex-start' }}>
				<div className='checkoutContainer'>
					<CheckoutStep
						stepNumber={'1'}
						title={'Login'}
						active={!auth.authenticate}
						body={
							auth.authenticate ? (
								<div className='loggedInId'>
									<span style={{ fontWeight: 500 }}>{auth.user.fullName}</span>
									<span style={{ fontWeight: 500 }}>{auth.user.email}</span>
								</div>
							) : (
								<div>
									<MaterialInput label='Email' />
								</div>
							)
						}
					/>

					<CheckoutStep
						stepNumber={'2'}
						title={'DELIVERY ADDRESS'}
						active={!confirmAddress && auth.authenticate}
						body={
							<>
								{confirmAddress ? (
									<div> {`${selectedAddress.address}`}</div>
								) : (
									address.map((adr) => (
										<Address
											selectAddress={selectAddress}
											enableAddressEditForm={enableAddressEditForm}
											confirmDeliveryAddress={confirmDeliveryAddress}
											onAddressSubmit={onAddressSubmit}
											adr={adr}
										/>
									))
								)}
							</>
						}
					/>

					{confirmAddress ? null : newAddress ? (
						<AddressForm onSubmitForm={onAddressSubmit} onCancel={() => {}} />
					) : (
						<CheckoutStep
							stepNumber='+'
							title='ADD NEW ADDRESS'
							active={false}
							onClick={() => setNewAddress(true)}
						/>
					)}

					<CheckoutStep 
                    stepNumber={'3'} 
                    title={'ORDER SUMMARY'} 
                    active={orderSummary} 
                    body={
                        orderSummary ? <CartPage onlyCartItems={true } /> : null
                    }
                    />

<Card>
<div>
    <p>Order con</p>
</div>
</Card>
					<CheckoutStep 
                    stepNumber={'4'} 
                    title={'PAYMENT OPTIONS'} 
                    
                    
                    />
				
                
                </div>
				<PriceDetails
					totalItem={Object.keys(cart.cartItems).reduce(function (qty, key) {
						return qty + cart.cartItems[key].qty;
					}, 0)}
					totalPrice={Object.keys(cart.cartItems).reduce((totalPrice, key) => {
						const { price, qty } = cart.cartItems[key];
						return totalPrice + price * qty;
					}, 0)}
				/>
			</div>
		</Layout>
	);
};

export default CheckoutPage;
