import React, { useEffect, useState } from 'react';
import './style.css';
// import flipkartLogo from '../../images/logo/flipkart.png';
// import goldenStar from '../../images/logo/golden-star.png';
import { IoIosArrowDown, IoIosCart, IoIosSearch } from 'react-icons/io';
import {
	Modal,
	MaterialInput,
	MaterialButton,
	DropdownMenu,
} from '../MaterialUI';
import { useDispatch, useSelector } from 'react-redux';
import { login, signout, register } from '../../actions';

/**
 * @author
 * @function Header
 **/

const Header = (props) => {
	const [loginModal, setLoginModal] = useState(false);
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');
	const [firstName, setFirstName] = useState('');
	const [lastName, setLastName] = useState('')
	const[signup, setSignup] = useState('');
	const auth = useSelector(state => state.auth)
  const dispatch = useDispatch();

	const userLogin = () => {
		dispatch(login({ email, password }));
	};

	const userRegister = () => {
		dispatch(register({ email, password, firstName, lastName }));
	};

	const logout = () => {
		dispatch(signout());
	}

  useEffect(() => {
if(auth.authenticate){
setLoginModal(false)
}
  }, [auth.authenticate]);
  const renderLoggedInMenu = () => {

    return(
      <DropdownMenu
      menu={
        <a className="fullName">
          {auth.user.fullName}
        </a>
      }
      menus={[
        { label: 'My Profile', href: '', icon: null },
        { label: 'Flipkart Plus Zone', href: '', icon: null },
        { label: 'Orders', href: `/account/orders`, icon: null },
        { label: 'Wishlist', href: '', icon: null },
        { label: 'Gift Cards', href: '', icon: null },
        { label: 'Rewards', href: '', icon: null },
        { label: 'Notification', href: '', icon: null },
        { label: 'Logout', href: '', icon: null, onClick: logout},
      ]}
    />
    );
   

  }

  const renderNonLoggedInMenu = () => {
    return(
      <DropdownMenu
						menu={
							<a 
							className='loginButton'
							 onClick={() => {setSignup(false); setLoginModal(true)}}>
								Login
							</a>
						}
						menus={[
							{ label: 'My Profile', href: '', icon: null },
							{ label: 'Flipkart Plus Zone', href: '', icon: null },
							{ label: 'Orders', href: '/account/orders', icon: null, 
						onClick: () => {
							!auth.authenticate && setLoginModal(true); 
						}
						},
							{ label: 'Wishlist', href: '', icon: null },
							{ label: 'Chats', href: '', icon: null },
							{ label: 'Coupons', href: '', icon: null },
						]}
						firstMenu={
							<div className='firstmenu'>
								<span>New Customer?</span>
								<a 
								onClick={() => {
									setLoginModal(true);
									setSignup(true )
								}}
								
								style={{ color: '#590a5b' }}>Sign Up</a>
							</div>
						}
					/>
    );
  }
	return (
		<div className='header'>
			<Modal visible={loginModal} onClose={() => setLoginModal(false)}>
				<div className='authContainer'>
					<div className='row'>
						<div className='leftspace'>
							<h2>Login</h2>
							<p>Get access to your Orders, Wishlist and Recommendations</p>
						</div>
						<div className='rightspace'>
						
						<div className="loginInputContainer">
						
							{signup && (
								<MaterialInput
								type='text'
								label='Enter First Name'
								value={email}
								onChange={(e) => setFirstName(e.target.value)}
							/>
							)}
							
							{signup && (
								<MaterialInput
								type='text'
								label='Enter Last Name'
								value={email}
								onChange={(e) => setLastName(e.target.value)}
							/>
							)}
							<MaterialInput
								type='text'
								label='Enter Email/Enter Mobile Number'
								value={email}
								onChange={(e) => setEmail(e.target.value)}
							/>

							<MaterialInput
								type='password'
								label='Enter Password'
								value={password}
								onChange={(e) => setPassword(e.target.value)}
								//	rightElement={<a href='#'>Forgot?</a>}
							/>

							<MaterialButton
								title='Login'
								bgColor='#590a5b'
								textColor='#ffffff'
								style={{
									margin: '40px 0 20px 0',
								}}
								onClick={userLogin}
							/>

							<p style={{textAlign: 'canter'}}>Or</p>

							<MaterialButton
								title='Request OTP'
								bgColor='#590a5b'
								textColor='#ffffff'
								style={{
									margin: '20px 0',
								}}
							/>
							</div>
						</div>
					</div>
				</div>
			</Modal>
		
		
			{/* 	<Modal visible={loginModal} onClose={() => setLoginModal(false)}>
				<div className='authContainer'>
					<div className='row'>
						<div className='leftspace'>
							<h2>Login</h2>
							<p>Get access to your Orders, Wishlist and Recommendations</p>
						</div>
						<div className='rightspace'>
						
						<div className="loginInputContainer">
						
							
								<MaterialInput
								type='text'
								label='Enter First Name'
								value={email}
								onChange={(e) => setFirstName(e.target.value)}
							/>
							
							
							
								<MaterialInput
								type='text'
								label='Enter Last Name'
								value={email}
								onChange={(e) => setLastName(e.target.value)}
							/>
							

							<MaterialInput
								type='text'
								label='Enter Email/Enter Mobile Number'
								value={email}
								onChange={(e) => setEmail(e.target.value)}
							/>

							<MaterialInput
								type='password'
								label='Enter Password'
								value={password}
								onChange={(e) => setPassword(e.target.value)}
								//	rightElement={<a href='#'>Forgot?</a>}
							/>

							<MaterialButton
								title='Register'
								bgColor='#590a5b'
								textColor='#ffffff'
								style={{
									margin: '40px 0 20px 0',
								}}
								onClick={userLogin}
							/>

							<p style={{textAlign: 'canter'}}>Or</p>

							<MaterialButton
								title='Request OTP'
								bgColor='#590a5b'
								textColor='#ffffff'
								style={{
									margin: '20px 0',
								}}
							/>
							</div>
						</div>
					</div>
				</div>
			</Modal>
		 */}
		
		
			<div className='subHeader'>
				{/* Logo Start here */}

				<div className='logo'>
					<a href='/' style={{textDecoration: 'none', color: '#ffffff' }}>
						{/* <img src={flipkartLogo} className="logoimage" alt="" /> */}
						<h2 style={{fontSize: '16px', textTransform: 'uppercase'  }}>Josebek Store </h2>
					</a>
				</div>

				{/* Logo ends here */}
				{/* Search start here */}
				<div
					style={{
						padding: '0 10px',
					}}
				>
					<div className='searchInputContainer'>
						<input
							className='searchInput'
							placeholder={'search for products, brands and more'}
						/>
						<div className='searchIconContainer'>
							<IoIosSearch
								style={{
									color: '#590a5b',
								}}
							/>
						</div>
					</div>
				</div>
				{/* Search ends here */}

				{/* Right side menu Start her */}
				<div className='rightMenu'>
					{auth.authenticate ? renderLoggedInMenu() : renderNonLoggedInMenu()}
					<DropdownMenu
						menu={
							<a className='more'>
								<span>More</span>
								<IoIosArrowDown />
							</a>
						}
						menus={[
							{ label: 'Notification Preference', href: '', icon: null },
							{ label: 'Sell on flipkart', href: '', icon: null },
							{ label: '24x7 Customer Care', href: '', icon: null },
							{ label: 'Advertise', href: '', icon: null },
							{ label: 'Download App', href: '', icon: null },
						]}
					/>
					<div>
						<a href={`/cart`} className='cart'>
							<IoIosCart />
							<span style={{ margin: '0 10px' }}>Cart</span>
						</a>
					</div>
				</div>

				{/* Right Side Menu Ends here */}
			</div>
		</div>
	);
};

export default Header;
